create table lmv_bright.inre_norrland_roads (
    gid int primary key,
    kkod integer,
    kategori varchar(50),
    vagnr1 varchar(8),
    vagnr2 varchar(8),
    vagnr3 varchar(8),
    vagnr4 varchar(8),
    bklass smallint,
    hmark smallint,
    the_geom geometry
);

insert into lmv_bright.inre_norrland_roads
    select
        vagk_vl.gid,
        kkod,
        kategori,
        vagnr1,
        vagnr2,
        vagnr3,
        vagnr4,
        bklass,
        hmark,
        st_difference(ST_SetSRID(vagk_vl.the_geom, 3006), ST_SetSRID(c.the_geom, 3006)) AS d
    from vagk_vl, lmv_bright.terrang_coverage_simple as c;
